import UsersList from "components/UsersList";
import React from "react";
import { useQuery } from "react-query";
import { getUserSuggestions } from "../utils/api-client";

export default function UserSuggestions({authUser}) {
  const {data, isLoading, isSuccess} =  useQuery("UserSuggestions", getUserSuggestions);

  const users = data?.filter(user => user.screen_name !== authUser?.screen_name && !user.following);

  if(!user?.legth) {
    return <div className="message">No user suggestions at the moment</div>
  }
  return <UsersList />;
}
